mapper.update:
UPDATE account_transaction
SET memo = #{memo}
WHERE id = #{transactionId}

mapper.select:
SELECT *
FROM account_transaction
WHERE account_id = #{accountId}

    AND tx_type = #{txType}

ORDER BY transaction_date_time DESC
    LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*)
FROM account_transaction
WHERE account_id = #{accountId}

    AND tx_type = #{txType}
